import React, { useState } from "react";
import './RegForm.css';

function RegForm(){
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    return(
        <div>
            <h2 class="page-title">Sign Up</h2>
            <div class="signup">
                <div class="username-container">
                    <label for="Username">Username:</label>
                    <input type="text" id="username" name="username" required onChange={(e) => setUsername(e.target.value)}></input>
                </div>
                <div class="password-container">
                    <label for="password">Password:</label>
                    <input type="password" id="password" name="password" required onChange={(e) => setPassword(e.target.value)}></input>
                </div>
                <div class="password-container">
                    <label for="confirm-password">Confirm Password:</label>
                    <input type="password" id="confirm-password" name="password" required onChange={(e) => setConfirmPassword(e.target.value)}></input>
                </div>
                <div class="email-container">
                    <label for="email">Email:</label>
                    <input type="text" id="email" name="email" required onChange={(e) => setEmail(e.target.value)}></input>
                </div>
                <button class="signup-button" onclick="signup()">Sign Up</button>
            </div>
        </div>
    );
}

export default RegForm;
