import React, { useState } from "react";
import './RegForm.css';

function RegForm(){
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [email, setEmail] = useState('');
    const [usernameErrors, setUsernameErrors] = useState([]);
    const [passwordErrors, setPasswordErrors] = useState([]);
    const [emailErrors, setEmailErrors] = useState([]);
    const [message, setMessage] = useState('');

    function validateUserName(){
        let usernameRegex = /^[a-zA-Z][a-zA-Z0-9_-]{2,19}$/;
        if(usernameRegex.test(username)){
            return true;
        }
        else{
            let usernameLengthRegex = /^.{3,20}$/;
            let allowedCharRegex = /^[a-zA-Z0-9_-]+$/;
            let letterStartRegex = /^[a-zA-Z]/;
            let noSpaces = /^\S+$/;
            if(!usernameLengthRegex.test(username)){
                setUsernameErrors(usernameErrors.push("Username must be between 3 and 20 characters"));
            }

            if(!allowedCharRegex.test(username)) {
                setUsernameErrors(usernameErrors.push("Username must only contain letters, numbers, hyphens (-), and underscores (_)"));
            }
            if(!letterStartRegex.test(username)){
                setUsernameErrors(usernameErrors.push("Username must start with a letter"));
            }
            if(!noSpaces.test(username)){
                setUsernameErrors(usernameErrors.push("Username cannot contain spaces"));
            }
            return false;
        }
    }


    function validatePassword(){
        let passwordRegex = /^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*()\-=+\[\]{};:'",.<>?/~])(?!.* ).{8,}$/;
        if(passwordRegex.test(password)){
            return true;
        }
        else{
            let passwordLengthRegex = /^.{8,}$/;
            let specialCharRegex =/(?=.*[!@#$%^&*()\-=+\[\]{};:'",.<>?/~])/;
            let numberRegex = /(?=.*[0-9])/;
            let capitalRegex = /(?=.*[A-Z])/;
            let lowercaseRegex = /(?=.*[a-z])/;
            let noSpaces = /^\S+$/;
            if(!passwordLengthRegex.test(password)){
                setPasswordErrors(passwordErrors.push("Must be at least 8 characters long"));
            }
            if(!specialCharRegex.test(password)){
                setPasswordErrors(passwordErrors.push("Must contain at least 1 special character"));
            }
            if(!numberRegex.test(password)){
                setPasswordErrors(passwordErrors.push("Must contain at least 1 number"));
            }
            if(!capitalRegex.test(password)){
                setPasswordErrors(passwordErrors.push("Must contain at least 1 capital letter"));
            }
            if(!lowercaseRegex.test(password)){
                setPasswordErrors(passwordErrors.push("Must contain at least 1 lowercase letter"));
            }
            if(!noSpaces.test(password)){
                setPasswordErrors(passwordErrors.push("Cannot contain spaces"));
            }
            return false;
        }

    }


    function validateConfirmPassword(){
        if(password == confirmPassword){
            return true;
        }
        else{
            return false;
        }
    }

    function validateEmail(){
        let emailRegex = /^(?!.* )[a-zA-Z0-9]+@[a-zA-Z0-9]+\.(com|net|ca|io)$/;
        emailErrors = [];
        if(emailRegex.test(email)){
            return true;
        }
        else{
            emailErrors.push("Must be a valid email address forrmat (e.g. username@example.com)")
            let noSpaces = /^\S+$/;
            let containsAt = /@/
            if(!noSpaces.test(email)){
                setEmailErrors(emailErrors.push("Cannot contain spaces"));
            }
            if(!containsAt.test(email)){
                setEmailErrors(emailErrors.push("Must contain the @ symbol"));
            }
            return false;
        }
    }

    const handleSubmit = (e) => {
        let usernameValid = validateUserName();
        let passwordValid = validatePassword();
        let confirmPasswordValid = validateConfirmPassword();
        let emailValid = validateEmail();

        if(usernameValid && passwordValid && confirmPasswordValid && emailValid){
            setMessage("Signup successful! Redirecting to login...");
            setMessageType('success');
            setTimeout(() => {
                window.location.href = "/Login";
            }, 2000);

        }
        else{
            errorMessage();
        }
    }

    

    
    




    return(
        <div>
            <h2 class="page-title">Sign Up</h2>
            <div class="signup">
                <div class="username-container">
                    <label for="Username">Username:</label>
                    <input type="text" id="username" name="username" onChange={(e) => setUsername(e.target.value)}></input>
                </div>
                <div class="password-container">
                    <label for="password">Password:</label>
                    <input type="password" id="password" name="password"onChange={(e) => setPassword(e.target.value)}></input>
                </div>
                <div class="password-container">
                    <label for="confirm-password">Confirm Password:</label>
                    <input type="password" id="confirm-password" name="password" onChange={(e) => setConfirmPassword(e.target.value)}></input>
                </div>
                <div class="email-container">
                    <label for="email">Email:</label>
                    <input type="text" id="email" name="email" onChange={(e) => setEmail(e.target.value)}></input>
                </div>
                <button class="signup-button" onclick="signup()">Sign Up</button>
            </div>
        </div>
    );
}

export default RegForm;
