import React from "react";

function SignupMessage({message, usernameErrors, passwordErrors, emailErrors, messageType, confirmPasswordValid}){

        return (
            <div id="dynamicBox">
                <div>
                    {usernameErrors.length === 0 ? (
                        <p>Valid Username</p>
                    ) : (
                        <p>Invalid Username: ({usernameErrors.join(', ')})</p>
                    )}
                </div>
    
                <div>
                    {passwordErrors.length === 0 ? (
                        <p>Valid Password</p>
                    ) : (
                        <p>Invalid Password: ({passwordErrors.join(', ')})</p>
                    )}
                </div>
    
                <div>
                    {confirmPasswordValid ? (
                        <p>Passwords Match</p>
                    ) : (
                        <p>Passwords Do Not Match</p>
                    )}
                </div>
    
                <div>
                    {emailErrors.length === 0 ? (
                        <p>Valid Email</p>
                    ) : (
                        <p>Invalid Email: ({emailErrors.join(', ')})</p>
                    )}
                </div>
            </div>
        );
    }


export default SignupMessage;
